const API_URL = "https://react-shopping-list.firebaseio.com/matches.json";

// let matches = [];
let matchData = {};
const getMatches = async function() {
  const resMatches = await axios
    .get(API_URL)
    .then(function(res) {
      console.log("res 13", res);
      matchData = res;
    })
    .catch(function(err) {
      console.log("err 17", err);
    });
};

setTimeout(() => {
  getMatches();
}, 5000);

// matches = matchData.data;
