import React, { useEffect, useState } from "react";

export default function Home() {
  const [homeState, setHomeState] = useState(0);
  const [awayState, setAwayState] = useState(0);
  const [currentInning, setInning] = useState("Bottom 1st");
  const [outs, setOuts] = useState(0);

  useEffect(() => {
    document.title = `The score is ${homeState} vs ${awayState}`;
  });

  function nextInning() {
    if (INNINGS.indexOf(currentInning) === 11) {
      return INNINGS(INNINGS.indexOf(currentInning) + 1);
    } else {
      return "Game Over";
    }
  }

  useEffect(() => {
    nextInning();
  });

  const iterateValue = function(e) {
    return [e.target.value] + 1;
  };

  const INNINGS = [
    "Bottom 1st",
    "Top 1st",
    "Bottom 2nd",
    "Top 2nd",
    "Bottom 3rd",
    "Top 3rd",
    "Bottom 4th",
    "Top 4th",
    "Bottom 5th",
    "Top 5th",
    "Bottom 6th",
    "Top 6th"
  ];

  return (
    <div>
      <h1>ProKickballer</h1>
      <div>
        <h2>Game Status</h2>
        <h3>Inning: {currentInning}</h3>
        <button>Balls: 0</button>
        <button>Strikes: 0</button>
        <button>Fouls: 0</button>
        <button onClick={() => setOuts(outs + 1)}>Outs: 0</button>
      </div>
      <div>
        <h3>Home Team</h3>
        <button onClick={() => setHomeState(homeState + 1)}>
          Score: {homeState}
        </button>
      </div>
      <div>
        <h3>Away Team</h3>
        <button onClick={() => setAwayState(awayState + 1)}>
          Score: {awayState}
        </button>
      </div>
    </div>
  );
}
