import React, { useContext, useState, useEffect } from "react";
import MatchContext from "./../../store/match-context";

export default function Match(props) {
  const context = useContext(MatchContext);
  console.log("Match 6 context", context);
  console.log("Match 7 props", props);
  // const [hName, sethName] = useState("Home Team");
  // const [homeState, setHomeState] = useState(0);
  // const [aName, setaName] = useState("Away Team");
  // const [awayState, setAwayState] = useState(0);
  // const [currentInning, setInning] = useState(0);
  // const [balls, setBalls] = useState(0);
  // const [strikes, setStrikes] = useState(0);
  // const [fouls, setFouls] = useState(0);
  // const [outs, setOuts] = useState(0);

  const { match, matches, innings, updateMatch } = context;
  let {
    awayTeamScore,
    balls,
    currentInning,
    fouls,
    homeTeamScore,
    outs,
    strikes
  } = match;

  const INNINGS = [
    "Top 1st",
    "Bottom 1st",
    "Top 2nd",
    "Bottom 2nd",
    "Top 3rd",
    "Bottom 3rd",
    "Top 4th",
    "Bottom 4th",
    "Top 5th",
    "Bottom 5th",
    "Top 6th",
    "Bottom 6th",
    "MATCH OVER"
  ];

  function resetMatch() {
    setBalls(0);
    setStrikes(0);
    setFouls(0);
  }

  function incrementBalls() {
    setBalls(balls + 1);
  }

  function incrementStrikes() {
    setStrikes(strikes + 1);
  }

  function incrementFouls() {
    setFouls(fouls + 1);
  }

  function incrementOuts() {
    setOuts(outs + 1);
    resetMatch();
  }

  function handleSubmit() {}

  useEffect(() => {
    document.title = `Home: ${homeState} vs Away: ${awayState}`;
    if (fouls === 4 || strikes === 3) {
      resetMatch();
      incrementOuts();
    }
    if (outs === 3) {
      setInning(currentInning + 1);
      setOuts(0);
    }
    if (balls === 4) {
      console.log("balls, now walk", balls);
      resetMatch();
    }
  });

  return (
    <React.Fragment>
      <div>
        <h1>ProKickballer</h1>
        <div>
          <h2>Game Status</h2>
          <h3>Inning: {INNINGS[currentInning]}</h3>
          <button onClick={incrementBalls}>Balls: {context.balls}</button>
          <button onClick={incrementStrikes}>Strikes: {context.strikes}</button>
          <button onClick={incrementFouls}>Fouls: {context.fouls}</button>
          <button onClick={incrementOuts}>Outs: {context.outs}</button>
        </div>
        <div>
          <h3>{hName}</h3>
          <button onClick={() => setHomeState(homeState + 1)}>
            Score: {homeState}
          </button>
        </div>
        <div>
          <h3>{aName}</h3>
          <button onClick={() => setAwayState(awayState => awayState + 1)}>
            Score: {awayState}
          </button>
        </div>
      </div>
    </React.Fragment>
  );
}
