import React, { useEffect, useState } from "react";
const API_URL = "//localhost:8080/api/ver0001/matches";

export default function Match0001() {
  const [hName, sethName] = useState("Home Team");
  const [homeState, setHomeState] = useState(0);
  const [aName, setaName] = useState("Away Team");
  const [awayState, setAwayState] = useState(0);
  const [currentInning, setInning] = useState(0);
  const [balls, setBalls] = useState(0);
  const [strikes, setStrikes] = useState(0);
  const [fouls, setFouls] = useState(0);
  const [outs, setOuts] = useState(0);

  const INNINGS = [
    "Top 1st",
    "Bottom 1st",
    "Top 2nd",
    "Bottom 2nd",
    "Top 3rd",
    "Bottom 3rd",
    "Top 4th",
    "Bottom 4th",
    "Top 5th",
    "Bottom 5th",
    "Top 6th",
    "Bottom 6th",
    "MATCH OVER"
  ];

  function resetMatch() {
    setBalls(0);
    setStrikes(0);
    setFouls(0);
  }

  function incrementBalls() {
    setBalls(balls + 1);
  }

  function incrementStrikes() {
    setStrikes(strikes + 1);
  }

  function incrementFouls() {
    setFouls(fouls + 1);
  }

  function incrementOuts() {
    setOuts(outs + 1);
    resetMatch();
  }

  function handleSubmit(e) {
    const match = {
      homeTeamScore: homeState,
      awayTeamScore: awayState,
      currentInning,
      balls,
      strikes,
      fouls,
      outs
    };
    console.log("match 64", match);
    fetch(`${API_URL}`, {
      method: "post",
      headers: new Headers({
        "Content-Type": "application/json"
      }),
      body: JSON.stringify({ ...match })
    })
      .then(res => {
        console.log("res 73", res);
        if (!res.ok) {
          if (res.status >= 400 && res.status < 500) {
            return res.json().then(data => {
              console.log("data.message 77", data.message);
              let err = {
                err: data.message
              };
              console.log("err 81", err);
              return err;
            });
          } else {
            let err = {
              errMessage: "Please Try Again Later. Server Is NOT Responding."
            };
            console.log("err 88", err);
            return err;
          }
        }
        console.log("res 92", res);
        res.json();
      })
      .then(match => {
        console.log("match 95", match);
        return match;
      });
  }

  // function handleChange(e) {
  //   console.log("hName", hName);
  //   sethName({ [e.target.name]: e.target.value });
  // }

  useEffect(() => {
    document.title = `Home: ${homeState} vs Away: ${awayState}`;
    if (fouls === 4 || strikes === 3) {
      resetMatch();
      incrementOuts();
    }
    if (outs === 3) {
      setInning(currentInning + 1);
      setOuts(0);
    }
    if (balls === 4) {
      console.log("balls, now walk", balls);
      resetMatch();
    }
  });

  return (
    <div>
      <h1>ProKickballer</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <h2>Game Status</h2>
          <h3>Inning: {INNINGS[currentInning]}</h3>
          <button onClick={incrementBalls}>Balls: {balls}</button>
          <button onClick={incrementStrikes}>Strikes: {strikes}</button>
          <button onClick={incrementFouls}>Fouls: {fouls}</button>
          <button onClick={incrementOuts}>Outs: {outs}</button>
        </div>
        <div>
          <h3>{hName}</h3>
          {/* <input
            id="home-team-name"
            key="hName"
            type="text"
            name="hName"
            className="form-control"
            autoComplete="off"
            value={hName}
            onChange={handleChange}
          /> */}
          <button onClick={() => setHomeState(homeState + 1)}>
            Score: {homeState}
          </button>
        </div>
        <div>
          <h3>{aName}</h3>
          <button onClick={() => setAwayState(awayState => awayState + 1)}>
            Score: {awayState}
          </button>
        </div>
        <button type="submit" className="btn">
          Submit
        </button>
      </form>
    </div>
  );
}
